import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactDOM from 'react-dom';
import Axios from 'axios';

class Button extends Component {
  constructor(props){
    super(props)

    this.state =
    {
      games: this.props.array





    }

  }

  getAllGames = () => {
    Axios.get('http://localhost:8080/IGDB/api/game/getAllGames').then(response => {
      this.setState({
        games:response.data
      });
    });
  }

  render() {

    console.log(this.props.buttonColour);
    console.log(this.state.fruit);
    return (
      <div className="App">

  <BootstrapTable
  data={this.state.games}

  striped

  search
>
  <TableHeaderColumn Column width={'20%'} dataField="gameTitle" isKey dataAlign="right" dataSort>Game Title</TableHeaderColumn>
  <TableHeaderColumn Column width={'30%'}dataField="gamePlatform" dataSort>Game Platform</TableHeaderColumn>
  <TableHeaderColumn Column width={'50%'} dataField="gameGenre" dataAlign="center" >Game Genre</TableHeaderColumn>
</BootstrapTable>
      </div>
    );
  }
}

export default Button;
