import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

// ReactDOM.render(<CreateGame/>)

class DeleteGame extends Component {
  constructor(props){
    super(props);
    this.state={
      deleteGame: false
    }
  }

  activateFields = (event) =>{
    this.setState({deleteGame:true});
    this.setState({reloadPage:true});
  }
  reloadPage = (event) => {
      window.location.reload()
  }

  deleteGame = (event) => {
    event.preventDefault();
    var data ={
      gameID:document.getElementById('gameID').value,
    }
    axios.delete('http://localhost:8080/IGDB/api/game/deleteGame/' + document.getElementById('gameID').value, data, {headers:{ crossorigin:true}}).then((res) => 
    {window.location.reload()});

  } 
  render() {
      return(
        <div>
        {this.state.deleteGame?
        <div>
        <input id="gameID" className ="form-control" type = "text" placeholder = "ID of Game" style={{width:'400px'}} />
        {/* Enter ID of game */}
        <br/>
        <button className= "btn btn-danger" dataAlign="right" onClick={this.deleteGame}>Submit</button>
        <button className= "btn btn-warning" dataAlign="right" onClick={this.reloadPage}>Back</button>
        </div>:<button className= "btn btn-outline-danger" dataAlign="right" onClick={this.activateFields}>Delete Game</button>}
        </div>
    );
  }
}

export default DeleteGame;
