import React, { Component } from 'react';
import './App.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';
import DeleteGame from './DeleteGame';
import UpdateGame from './UpdateGame';
import ReactDOM from 'react-dom';


class GetGames extends Component {
  constructor(props){
    super(props)
    this.state={
      loaded: false
    }
  }

  componentWillMount = () => {
    console.log("mount")
    axios.get('http://localhost:8080/IGDB/api/game/getAllGames').then(response => {
      this.setState({
        loaded: true,
        games: response.data,
        rating: response.data.ratings
      });
      console.log(response.data)
    });
  }

  createDeleteButton = (cell, rows) => {
    return <button id={rows.gameID} className="btn btn-danger" onClick={() => this.deleteFunction()}>Delete</button>;
    }
  createUpdateButton = (cell, rows) => {
    return <button id={rows.updateButton} className="btn btn-warning" onClick={() => this.updateFunction()}>Update</button>;
    }

  // deleteGame = (event) => {
  //   // event.preventDefault();
  //   var data ={
  //     gameID:document.getElementById('gameID').value = 254,
  //   }
  //   axios.delete('http://localhost:8080/IGDB/api/game/deleteGame/' + document.getElementById('gameID').value, data, {headers:{ crossorigin:true}}).then((res) => 
  //   {window.location.reload()});

  // } 



  deleteFunction = () => {
  ReactDOM.render(<DeleteGame/>);
  }
  updateFunction = () => {
  ReactDOM.render(<UpdateGame />);
  }

  render() {
    const loaded = this.state.loaded;
    return (
    <div className="GetGames">
      {loaded ?
      <BootstrapTable
      data={this.state.games}
      striped
      bordered
      search
      >
      <TableHeaderColumn Column width={'5%'} dataField="gameID" isKey dataAlign="right" dataSort>ID</TableHeaderColumn>
      <TableHeaderColumn Column width={'20%'}dataField="gameName" dataAlign="right"dataSort >Name</TableHeaderColumn>
      <TableHeaderColumn Column width={'5'} dataField="yearOfRelease" dataAlign="right" >Year Released</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="genre" dataAlign="right" >Genre</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="developer" dataAlign="right" >Developer</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="publisher" dataAlign="right" >Publisher</TableHeaderColumn>
      <TableHeaderColumn Column width={'5%'} dataField="ageRating" dataAlign="right" >Age Rating</TableHeaderColumn>
      <TableHeaderColumn Column width={'15%'} dataField="viewRating" ddataAlign="right" >View/Add Rating and Comments</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="updateGame" dataAlign="right" dataFormat={this.createUpdateButton}>Update Game</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="deleteGame" dataAlign="right" dataFormat={this.createDeleteButton}>Delete Game</TableHeaderColumn>
      {/* <TableHeaderColumn Column width={'5%'} dataField="games.ratings" dataAlign="right" >Score Rating</TableHeaderColumn> */}
    </BootstrapTable>: <CircularProgress/>}
  </div>
  );

  }
}

export default GetGames;
