import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

// ReactDOM.render(<CreateGame/>)

class UpdateGame extends Component {
  constructor(props){
    super(props);
    this.state={
      updateGame: false
    }
  }

  activateFields = (event) =>{
    this.setState({updateGame:true});
  }
  reloadPage = (event) => {
      window.location.reload()
  }


  updateGame = (event) => {
    event.preventDefault();
    var data ={
      gameID:document.getElementById('gameID').value,
      gameName:document.getElementById('gameName').value,
      yearOfRelease:document.getElementById('yearOfRelease').value,
      genre:document.getElementById('genre').value,
      developer:document.getElementById('developer').value,
      publisher:document.getElementById('publisher').value,
      ageRating:document.getElementById('ageRating').value,
    }
    axios.put('http://localhost:8080/IGDB/api/game/updateGame/' + document.getElementById('gameID').value, data, {headers:{ crossorigin:true}}).then((res) => 
    {window.location.reload()});

    //   this.setState({createGame:false});
    //   document.getElementById('gameName').value = "";
    //   document.getElementById('yearOfRelease').value = "";
    //   document.getElementById('genre').value = "";
    //   document.getElementById('developer').value = "";
    //   document.getElementById('publisher').value = "";
    //   document.getElementById('ageRating').value = "";
    //   this.props.shouldUpdate();
  } 
  render() {
      return(
        <div>
            Updating {this.props.gameName} review
        <input id="gameID" value={this.props.updateID} className ="form-control" type = "text" placeholder = "ID of Game" style={{width:'400px'}} />
        {/* Enter ID of game */}
        <br/>
        <input id="gameName" className ="form-control" type = "text" placeholder = "Name of Game" style={{width:'400px'}} />
        {/* Enter name of game */}
        <br/>
        <input id = "yearOfRelease" className ="form-control" type = "text" placeholder = "Year Released" style={{width:'150px'}} />
        {/* Enter year of release of game */}
        <br/>
        <input id = "genre" className ="form-control" type = "text" placeholder = "Genre" style={{width:'250px'}} />
        {/* Enter genre of game */}
        <br/>
        <input id ="developer" className ="form-control" type ="text" placeholder = "Developer" style={{width:'250px'}} />
        {/* Enter developer name of game */}
        <br/>
        <input id ="publisher" className ="form-control" type ="text" placeholder = "Publisher" style={{width:'250px'}} />
        {/* Enter publisher name of game */}
        <br/>
        <input id ="ageRating" className ="form-control" type ="text" placeholder = "Age Rating" style={{width:'150px'}} />
        {/* Enter age rating of game */}
        <br/>
        <button className= "btn btn-info" dataAlign="right" onClick={this.updateGame}>Submit</button>
        <button className= "btn btn-warning" dataAlign="right" onClick={this.reloadPage}>Back</button>
        </div>
    );
  }
}

export default UpdateGame;
