import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactDOM from 'react-dom';
import axios from 'axios';

class GetGames extends Component {
  constructor(props){
    super(props)
    this.setState({
      loaded: false
    })
  }

  // shouldComponentUpdate = (nextProps, nextState) => {
  //   if(this.props!=nextProps || this.state!=nextState){
  //     console.log("different")
  //     return true
  //   }
  //   console.log("same")
  //   return false
  // }

  componentWillMount = () => {
    console.log("mount")
    axios.get('http://localhost:8080/IGDB/api/game/getAllGames').then(response => {
      this.setState({
        loaded: true,
        games: response.data,
        rating: response.data.ratings
      });
      console.log(response.data)
    });
  }

  render() {

    return (
    <div className="App">
    {this.state}?
    <BootstrapTable
    data={this.state.games}

    striped

    search
    >
    <TableHeaderColumn Column width={'5%'} dataField="gameID" isKey dataAlign="right" dataSort>ID</TableHeaderColumn>
    <TableHeaderColumn Column width={'35%'}dataField="gameName" dataAlign="right"dataSort>Name</TableHeaderColumn>
    <TableHeaderColumn Column width={'5'} dataField="yearOfRelease" dataAlign="right" >Year Released</TableHeaderColumn>
    <TableHeaderColumn Column width={'15%'} dataField="genre" dataAlign="right" >Genre</TableHeaderColumn>
    <TableHeaderColumn Column width={'15%'} dataField="developer" dataAlign="right" >Developer</TableHeaderColumn>
    <TableHeaderColumn Column width={'15%'} dataField="publisher" dataAlign="right" >Publisher</TableHeaderColumn>
    <TableHeaderColumn Column width={'5%'} dataField="ageRating" dataAlign="right" >Age Rating</TableHeaderColumn>
    {/* <TableHeaderColumn Column width={'5%'} dataField="viewRating" ddataAlign="right" >View Rating and Comments</TableHeaderColumn>
    <TableHeaderColumn Column width={'5%'} dataField="addRating" dataAlign="right" >Add Rating and Comment</TableHeaderColumn>
    <TableHeaderColumn Column width={'5%'} dataField="updateGame" dataAlign="right" >Update Game</TableHeaderColumn>
    <TableHeaderColumn Column width={'5%'} dataField="deleteGame" dataAlign="right" >Delete Game</TableHeaderColumn> */}
    {/* <TableHeaderColumn Column width={'5%'} dataField="games.ratings" dataAlign="right" >Score Rating</TableHeaderColumn> */}
  </BootstrapTable>
        </div>
      );
  }
}

export default GetGames;
