import React, { Component } from 'react';
import './App.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';



class GetRatings extends Component {
  constructor(props){
    super(props)
    this.state={
      loaded: false
    }
  }

  componentWillMount = () => {
    console.log("mount")
    axios.get('http://localhost:8080/AdilsRecipes/rest/recipes/getRecipeRatings/').then(response => {
      this.setState({
        loaded: true,
        games: response.data,
        rating: response.data.ratings
      });
      console.log(response.data)
    });
  }

  render() {
    const loaded = this.state.loaded;
    return (
    <div className="GetGames">
      {loaded ?
      <BootstrapTable
      data={this.state.rating}
      striped
      bordered
      search
      >
      <TableHeaderColumn Column width={'5%'} dataField="ratingID" isKey dataAlign="right" dataSort>Rating ID</TableHeaderColumn>
      <TableHeaderColumn Column width={'5%'}dataField="gameID" dataAlign="right"dataSort >Game ID</TableHeaderColumn>
      <TableHeaderColumn Column width={'15'} dataField="name" dataAlign="right" >Name</TableHeaderColumn>
      <TableHeaderColumn Column width={'5%'} dataField="rating" dataAlign="right" >Rating</TableHeaderColumn>
      <TableHeaderColumn Column width={'50%'} dataField="comment" dataAlign="right" >Comment</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="updateRating" dataAlign="right" dataFormat={this.createUpdateButton}>Update Rating</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="deleteRating" dataAlign="right" dataFormat={this.createDeleteButton}>Delete Rating</TableHeaderColumn>
    </BootstrapTable>: <CircularProgress/>}
  </div>
  );

  }
}

export default GetRatings;
