import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactDOM from 'react-dom';
import axios from 'axios';

class CreateGame extends Component {

  createGame() {
    ReactDOM.render(<GetGames/>,document.getElementById('creatieGame'));
  }

  constructor(props){
    super(props)
  }

    createGame = (event) => {
      event.preventDefault();
      var data ={
        gameName:document.getElementById('gameName').value,
        yearOfRelease:document.getElementById('yearOfRelease').value,
        genre:document.getElementById('genre').value,
        developer:document.getElementById('developer').value,
        publisher:document.getElementById('publisher').value,
        ageRating:document.getElementById('ageRating').value,
      }
        axios.post('http://localhost:8080/IGDB/api/game/createGame',data).then((response) => {
          console.log(response.data);

        });
        document.getElementById('createGame').innerHTML='Game has been added';
        ReactDOM.render(<GetGames/>, document.getElementById('createGame'));
  } 
  render() {
      return(
        <div>
        Game ID
        <br/>
        <input id="gameName" className ="form-control" type = "number" placeholder = "Name of Game" style={{width:'150px'}} />
        Enter name of game
        <br/>
        <input id = "yearOfRelease" className ="form-control" type = "number" placeholder = "Year Released" style={{width:'150px'}} />
        Enter year of release of game
        <br/>
        <input id = "genre" className ="form-control" type = "number" placeholder = "Genre" style={{width:'150px'}} />
        Enter genre of game
        <br/>
        <input id ="developer" className ="form-control" type ="text" placeholder = "Developer" style={{width:'500px'}} />
        Enter developer name of game
        <br/>
        <input id ="publisher" className ="form-control" type ="text" placeholder = "Publisher" style={{width:'500px'}} />
        Enter publisher name of game
        <br/>
        <input id ="ageRating" className ="form-control" type ="text" placeholder = "Age Rating" style={{width:'500px'}} />
        Enter age rating of game
        <br/>
        <button className= "btn btn-outline-success" onClick={this.createGame}>Add Game</button>
        </div>
    );
  }
}

export default CreateGame;
