import React, { Component } from 'react';
import './App.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';
import DeleteGame from './DeleteGame';
import UpdateGame from './UpdateGame';
import ReactDOM from 'react-dom';
import GetRatings from './GetRatings';


class GetGames extends Component {
  constructor(props){
    super(props)
    this.state={
      loaded: false
    }
  }

  componentWillMount = () => {
    console.log("mount")
    axios.get('http://localhost:8080/IGDB/api/game/getAllGames').then(response => {
      this.setState({
        loaded: true,
        update: false,
        view:false,
        delete:false,
        games: response.data,
        rating: response.data.ratings
      });
      console.log(response.data)
    });
  }
  createRatingsButton = (cells, rows) => {
    return <button id={rows.gameID} className="btn btn-info" onClick={() => this.ratingsFunction(rows)}>View/Add</button>;
    }
  createDeleteButton = (rows) => {
    return <button id={rows.gameID} className="btn btn-danger" onClick={() => this.deleteFunction(rows)}>Delete</button>;
    }
  createUpdateButton = (cells, rows) => {
    console.log(rows)
    return <button id={rows.updateButton} className="btn btn-warning" onClick={() => this.updateFunction(rows)}>Update</button>;
    }

  deleteFunction = () => {
  ReactDOM.render(<DeleteGame/>);
  }
  updateFunction = () => {
    document.getElementById("update").setAttribute("updateID", 5);
    //document.getElementById('gameID').value = "lol";
    this.setState({update:true, ...this.state});
  }
  ratingsFunction = () => {
  ReactDOM.render(<GetRatings />);
  }

  render() {
    const loaded = this.state.loaded;
    return (
    <div className="GetGames" id="getGames">> 
    <UpdateGame id="update"/>
      {loaded ?
      <BootstrapTable
      data={this.state.games}
      striped
      bordered
      search
      >
      <TableHeaderColumn Column width={'5%'} dataField="gameID" isKey dataAlign="right" dataSort>ID</TableHeaderColumn>
      <TableHeaderColumn Column width={'25%'}dataField="gameName" dataAlign="right"dataSort >Name</TableHeaderColumn>
      <TableHeaderColumn Column width={'5'} dataField="yearOfRelease" dataAlign="right" >Year Released</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="genre" dataAlign="right" >Genre</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="developer" dataAlign="right" >Developer</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="publisher" dataAlign="right" >Publisher</TableHeaderColumn>
      <TableHeaderColumn Column width={'5%'} dataField="ageRating" dataAlign="right" >Age Rating</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="viewRating" dataAlign="right" dataFormat={this.createRatingsButton}>Ratings/Comments</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="updateGame" dataAlign="right" dataFormat={this.createUpdateButton}>Update Game</TableHeaderColumn>
      <TableHeaderColumn Column width={'10%'} dataField="deleteGame" dataAlign="right" dataFormat={this.createDeleteButton}>Delete Game</TableHeaderColumn>
    </BootstrapTable>: <CircularProgress/>}
  </div>
  );

  }
}

export default GetGames;
