import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactDOM from 'react-dom';
import axios from 'axios';
import GetGames from './GetGames';

// ReactDOM.render(<CreateGame/>)

class DeleteGame extends Component {
  constructor(props){
    super(props);
    this.state={
      deleteGame: false
    }
  }

  activateFields = (event) =>{
    this.setState({updateGame:true});
  }

  updateGame = (event) => {
    event.preventDefault();
    var data ={
      gameName:document.getElementById('gameName').value,
      yearOfRelease:document.getElementById('yearOfRelease').value,
      genre:document.getElementById('genre').value,
      developer:document.getElementById('developer').value,
      publisher:document.getElementById('publisher').value,
      ageRating:document.getElementById('ageRating').value,
    }
    axios.delete('http://localhost:8080/IGDB/api/game/deleteGame/' + document.getElementById('gameID').value, data, {headers:{ crossorigin:true}}).then((res) => 
    {window.location.reload()});

    //   this.setState({createGame:false});
    //   document.getElementById('gameName').value = "";
    //   document.getElementById('yearOfRelease').value = "";
    //   document.getElementById('genre').value = "";
    //   document.getElementById('developer').value = "";
    //   document.getElementById('publisher').value = "";
    //   document.getElementById('ageRating').value = "";
    //   this.props.shouldUpdate();
  } 
  render() {
      return(
        <div>
        {this.state.deleteGame?
        <div>
        <input id="gameID" className ="form-control" type = "text" placeholder = "ID of Game" style={{width:'400px'}} />
        {/* Enter ID of game */}
        <br/>
        <button className= "btn btn-outline-success" dataAlign="right" onClick={this.deleteGame}>Submit</button>
        </div>:<button className= "btn btn-outline-success" dataAlign="right" onClick={this.activateFields}>Delete Game</button>}
        </div>
    );
  }
}

export default DeleteGame;
