  <TableHeaderColumn Column width={'25%'}dataField="gameName" dataAlign="right"dataSort>Name</TableHeaderColumn>
  <TableHeaderColumn Column width={'10%'} dataField="yearOfRelease" dataAlign="right" >Year Released</TableHeaderColumn>
  <TableHeaderColumn Column width={'15%'} dataField="genre" dataAlign="right" >Genre</TableHeaderColumn>
  <TableHeaderColumn Column width={'15%'} dataField="developer" dataAlign="right" >Developer</TableHeaderColumn>
  <TableHeaderColumn Column width={'15%'} dataField="publisher" ddataAlign="right" >Publisher</TableHeaderColumn>
  <TableHeaderColumn Column width={'10%'} dataField="ageRating" dataAlign="right" >Age Rating</TableHeaderColumn>
</BootstrapTable>
      </div>
    );
  }
}

export default Button;
