import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactDOM from 'react-dom';

class Button extends Component {
  constructor(props){
    super(props)

    this.state =
    {
      games: 
      [
        {gameTitle: "Sonic",
        gamePlatform: "PS3",
        gameGenre: "Platformer"
      }
      ,
      {gameTitle: "Resident Evil",
        gamePlatform: "PS3",
        gameGenre: "Horror"
      }

      ]
    }

  }

  render() {

    console.log(this.props.buttonColour);
    return (
      <div className="App">

  <BootstrapTable
  data={this.state.games}

  striped

  search
>
  <TableHeaderColumn Column width={'20%'} dataField="gameTitle" isKey dataAlign="right" dataSort>Game Title</TableHeaderColumn>
  <TableHeaderColumn dataField="gamePlatform" dataSort>Game Platform</TableHeaderColumn>
  <TableHeaderColumn dataField="gameGenre" dataAlign="center" >Game Genre</TableHeaderColumn>
</BootstrapTable>
      </div>
    );
  }
}

export default Button;
